/**
 * EdgeOne Functions 打包脚本
 * 
 * 作用：将 functions/api/generate-source.js 和 shared/ 模块打包成单文件
 * 输出：functions/api/generate.js（部署到 EdgeOne）
 * 
 * 使用方法：
 * npm run build:functions
 */

import * as esbuild from 'esbuild';
import { fileURLToPath } from 'url';
import { dirname, resolve } from 'path';

const __filename = fileURLToPath(import.meta.url);
const __dirname = dirname(__filename);
const projectRoot = resolve(__dirname, '..');

async function bundle() {
  console.log('[Bundle] 开始打包 EdgeOne Functions...');
  
  try {
    await esbuild.build({
      entryPoints: [resolve(projectRoot, 'functions', 'api', 'generate-source.js')],
      bundle: true,
      outfile: resolve(projectRoot, 'functions', 'api', 'generate.js'),
      format: 'esm',
      platform: 'browser', // EdgeOne Functions 运行在类浏览器环境
      target: 'esnext',
      minify: false, // 开发时不压缩，方便调试
      sourcemap: false,
      external: [], // 打包所有依赖
      banner: {
        js: '// EdgeOne Functions - Auto-generated from generate-source.js\n// DO NOT EDIT THIS FILE DIRECTLY\n'
      }
    });
    
    console.log('[Bundle] ✅ 打包成功！');
    console.log('[Bundle] 输出文件: functions/api/generate.js');
    console.log('[Bundle] 现在可以部署到 EdgeOne Pages 了');
  } catch (error) {
    console.error('[Bundle] ❌ 打包失败:', error);
    process.exit(1);
  }
}

bundle();

